import { css } from "@emotion/css";

export const FormContainer = css`
  width: 100%;
`;
export const InputField = css`
  width: 100%;
  padding: 10px 20px;
  font-weight: 500;
  font-size: 14px;
  color: rgba(37, 67, 83, 0.4);
  font-family: Montserrat;
  border-radius: 3px;
  border: 0.5px solid #c7c7c7;
  box-sizing: border-box;
  border-radius: 3px;
`;
export const InputFieldPassword = css`
  width: 100%;
  padding: 10px 20px;
  font-weight: 500;
  font-size: 14px;
  color: rgba(37, 67, 83, 0.4);
  font-family: Montserrat;
  border-radius: 3px;
  border: 0.5px solid #c7c7c7;
  box-sizing: border-box;
  border-radius: 3px;
  font-weight: 900;
  letter-spacing: 0.15em;
`;
export const Label = css`
  font-weight: 600;
  font-size: 14px;
  color: rgba(37, 67, 83, 0.75);
  font-family: "Montserrat", sans-serif;
`;
export const SubmitButton = css`
  width: 100%;
  background: #57b3e4;
  border-radius: 35px;
  font-size: 18px;
  color: #ffffff;
  font-family: Montserrat;
  border: 0px;
  padding: 14px;
  font-weight: 700;
  cursor: pointer;
`;
